function difference_fini()a=0;b=1;N = 200;dx = (b-a)/N;dt = 0.4; a = 0.5; for i=1:NU1(i) = 0;U2(i) = 0;U_e(i) = 0;end%Solution à l'instant initialx = linspace(0,1,N);U0 = cos(2*pi.*x);%Calcul et affichage de la solution exactefor i=1:N    if ( x(i) - a*dt > 0 )    U_e(i) = cos(2*pi*(x(i) - a*dt));    else    U_e(i) = 1;    endend%Solution à l'instant t=0.1%Schema décentrer à droiteU1(1)=U0(1);for i=2:NU1(i)= U0(i) - a*(dt/dx)*(U0(i)-U0(i-1));end%Schema décentrer à gaucheU2(N)=1;for i=1:N-1U2(i) = U0(i) - a*(dt/dx)*(U0(i+1)-U0(i));end%Schema centréU3(1)=U0(1);U3(N)=1;for i=2:N-1U3(i) = U0(i) - a*(dt/(2*dx))*(U0(i+1)-U0(i-1));endfiguresubplot(2,2,1)plot(x,U1)title('U1')%subplot(2,2,2)%plot(x,U2)%title('U2')%subplot(2,2,3)%plot(x,U3)%title('U3')%subplot(2,2,4)%plot(x,U_e)%title('U_e')end